const sectionCenter =  document.querySelector(".section-center");
const container = document.querySelector('.btn-container')


// load items
window.addEventListener("DOMContentLoaded", function () {
    displayMenuItems(menu);
    displayMeunButtons(); 
});



function displayMenuItems(menuItems) {
    let displayMenu = menuItems.map(function(item){
        return `<article class="menu-item">
                <img alt="${item.title}" class="photo" src="${item.img}">
        <div class="item-info">
            <header>
                <h4>${item.title}</h4>
                <h4 class="price">$${item.price}</h4>
            </header>
            <p class="item-text">${item.desc}</p>
        </div>
    </article>`;
    });

    // el join es para a√±adir el nuevo contenido en una nueva lista.
    displayMenu = displayMenu.join("");
    sectionCenter.innerHTML = displayMenu;
}

function displayMeunButtons() {
    const categories = menu.reduce(function(values,item){
        if(!values.includes(item.category)) {
            values.push(item.category);
        }
        return values 
    },
    ['all']
    );
    const categoryBtns = categories.map(function(category){
        return `<button class="filter-btn" type="button" data-id="${category}">
        ${category}
    </button>`
    }).join("");
    container.innerHTML = categoryBtns;
    const filterBtns = document.querySelectorAll('.filter-btn');
    // Filter items
    filterBtns.forEach(function(btn){
        btn.addEventListener("click", function(e) {
            //console.log(e.currentTarget.dataset);
            //en categoria se encuentra los datos filstrados de los botones atraves de "data-id"
            const category = e.currentTarget.dataset.id;
            const menuCategory = menu.filter(function (menuItem) {
                if(menuItem.category === category) {
                    return menuItem; 
                }
            });
            if(category === 'all'){
                displayMenuItems(menu)
            }
            else {
                displayMenuItems(menuCategory);
            }
        });
    });
}